import * as BMapGL from "vue-baidu-map/types/map";
export default {
  components: {},

  data() {
    return {
      //商家信息(包含坐标信息)
      businessDetail: {},
      //地图数据
      mapData: {
        //中心坐标
        center: {
          lng: 0,
          lat: 0
        },
        //缩放级别
        zoom: 13
      },
      //BMap类
      BMap: null,
      //地图对象
      map: null
    };
  },

  created() {
    this.init();
  },

  methods: {
    init() {
      var map = new BMapGL.Map('map'); // 创建Map实例

      map.centerAndZoom(new BMapGL.Point(116.404, 39.915), 12); // 初始化地图,设置中心点坐标和地图级别

      map.enableScrollWheelZoom(true); // 开启鼠标滚轮缩放
    }

  }
};
