import Map from "@arcgis/core/Map";
import View from "@arcgis/core/views/MapView";
import MapImageLayer from "@arcgis/core/layers/MapImageLayer";
import BasemapGallery from "@arcgis/core/widgets/BasemapGallery";
import Expand from "@arcgis/core/widgets/Expand";
import Basemap from "@arcgis/core/Basemap";
export default {
  data() {
    return {};
  },

  beforeMount() {},

  created() {},

  mounted() {
    this.initMap();
  },

  methods: {
    initMap() {
      esriConfig.portalUrl = "https://linux111.esrichina.com/arcgis";
      const m_Map = new Map({
        basemap: "topo-vector"
      });
      const view = new View({
        container: "viewDiv",
        map: m_Map,
        center: [115, 32],
        zoom: 5
      });
      let layer = new MapImageLayer({
        url: "http://localhost:6080/arcgis/rest/services/graduate/MapServer"
      });
      console.log(layer);
      var expand = new Expand({
        //图层集
        content: new BasemapGallery({
          view: view,
          // 设定图层集有哪些图层
          source: [Basemap.fromId("topo-vector"), Basemap.fromId("hybrid"), Basemap.fromId("osm")] // autocasts to LocalBasemapsSource

        })
      }, "BasemapGallery");
      expand.expanded = false; // this.document.getElementById("BasemapGallery").onmouseover=function(){
      //   expand.expanded=true
      // }
      // this.document.getElementById("BasemapGallery").onmouseout=function(){
      //   expand.expanded=false
      // }

      m_Map.add(layer);
      view.ui.remove('attribution');
    }

  }
};
