      }).then(AMap => {
        var _this = this;

        this.map = new AMap.Map("container", {
          //设置地图容器id
          resizeEnable: true,
          mapStyle: 'amap://styles/darkblue',
          viewMode: "3D",
          //是否为3D地图模式
          zoom: 7,
          //初始化地图级别
          center: [118.3093, 32.3153],
          //初始化地图中心点位置
          doubleClickZoom: true,
          // 是否允许鼠标拖拽
          dragEnable: true,
          keyboardEnable: false
        });
        let marker;

        for (var i = 0; i < _this.lngLat.length; i++) {
          marker = new AMap.Marker({
            position: _this.lngLat[i],
            map: _this.map
          });
          marker.content = ["<h2>" + lngLats[i].city + "  " + lngLats[i].station + "</h2>  AQI指数:" + lngLats[i].aqi + "   CO指数:" + lngLats[i].co + "<br/>" + "NO2指数:" + lngLats[i].no2 + "   PM2.5指数:" + lngLats[i].pm25 + "<br/> PM10指数:" + lngLats[i].pm10 + "   SO2指数:" + lngLats[i].so2 + "<br/> O3指数:" + lngLats[i].o3 + "<br/>" + "<a href='https://ditu.amap.com/detail/B000A8URXB?citycode=110105'>详细信息</a>"];
          marker.on("click", this.markerClick); //marker.emit("click", { target: marker });
        }
      });
    },

    markerClick(e) {
      console.log("被点击了!");
      console.log(e.lnglat.lng); //offset用来偏移，第一个参数是左右，第一个参数是上下。左和上是负的，右和下是正的

      var infoWindow = new AMap.InfoWindow({
        offset: new AMap.Pixel(0, -30)
      });
      infoWindow.setContent(e.target.content);
      infoWindow.open(this.map, e.target.getPosition());
    },

    initdata() {
      this.request.get("/nowatmospheredata/GetAllquality").then(res => {
        for (var i = 0; i < res.length; i++) {
          if (res[i].quality === "良") {
            this.airnormal += 1;
          } else if (res[i].quality === "优") {
            this.airgood += 1;
          } else if (res[i].quality === "差") {
            this.airbad += 1;
          }
        }

        this.airall = this.airgood + this.airbad + this.airnormal;
      });
    }

  }
