import { loadModules } from "esri-loader";
export default {
  name: "web-map",

  mounted() {
    // lazy load the required ArcGIS API for JavaScript modules and CSS
    loadModules(['esri/Map', 'esri/views/MapView', 'esri/widgets/Expand', //展开
    'esri/layers/FeatureLayer', 'esri/widgets/BasemapGallery', //底图切换
    'esri/widgets/Legend', //图例功能
    'esri/widgets/Search', //查询
    'esri/widgets/LayerList', //图层列表
    'esri/widgets/Compass', //指南针
    'esri/widgets/CoordinateConversion', //地理坐标xy
    'esri/widgets/Measurement', "esri/layers/ImageryLayer"], {
      css: true
    }).then(([Map, MapView, Expand, FeatureLayer, BasemapGallery, Legend, Search, LayerList, Compass, CoordinateConversion, Measurement]) => {
      const map = new Map({
        basemap: "satellite",
        ground: "world-elevation"
      });
      const view = new MapView({
        container: "viewDiv",
        map: map
      });
    });
  },

  beforeUnmount() {
    if (this.view) {
      // destroy the map view
      this.view.container = null;
    }
  }

};
