import AMapLoader from '@amap/amap-jsapi-loader';
export default {
  data() {
    return {
      value1: true,
      map: null
    };
  },

  mounted() {
    //DOM初始化完成进行地图初始化
    this.initMap();
    this.createMap();
  },

  methods: {
    changValue() {
      if (this.value1) {
        this.map.setMapStyle('amap://styles/normal');
      } else {
        this.map.setMapStyle('amap://styles/macaron');
      }
    },

    initMap() {
      AMapLoader.load({
        key: "0e5904b2829e2e25ffcdecc5685b2209",
        // 申请好的Web端开发者Key，首次调用 load 时必填
        version: "2.0",
        // 指定要加载的 JSAPI 的版本，缺省时默认为 1.4.15
        plugins: ['AMap.ToolBar', 'AMap.Scale', 'AMap.MapType', 'AMap.Geolocation', 'AMap.HawkEye'] // 需要使用的的插件列表，如比例尺'AMap.Scale'等

      }).then(AMap => {
        this.map = new AMap.Map("container", {
          //设置地图容器id
          mapStyle: 'amap://styles/normal',
          viewMode: "3D",
          //是否为3D地图模式
          zoom: 12,
          //初始化地图级别
          center: [118.316264, 32.303627],
          //初始化地图中心点位置
          doubleClickZoom: true,
          // 是否允许鼠标拖拽
          dragEnable: true,
          keyboardEnable: false
        }); // 在图面添加工具条控件，工具条控件集成了缩放、平移、定位等功能按钮在内的组合控件

        this.map.addControl(new AMap.ToolBar()); // 在图面添加比例尺控件，展示地图在当前层级和纬度下的比例尺

        this.map.addControl(new AMap.Scale()); // 在图面添加鹰眼控件，在地图右下角显示地图的缩略图

        this.map.addControl(new AMap.HawkEye({
          isOpen: true
        })); // 在图面添加类别切换控件，实现默认图层与卫星图、实施交通图层之间切换的控制

        this.map.addControl(new AMap.MapType()); // 在图面添加定位控件，用来获取和展示用户主机所在的经纬度位置

        this.map.addControl(new AMap.Geolocation());
      }).catch(e => {
        console.log(e);
      });
    },

    createMap() {}

  }
};
