import Vue from 'vue'
import Router from 'vue-router'
Vue.use(Router)
import Store from '@/store'
import {userInfo} from '@/service/api.js'
import Login from '@/views/user-login/index.vue'



<template>
  <div class="login-section">
    <el-form
        label-position="top" label-width="100px" class="demo-ruleForm"
        :rules="rules"
        :model="rulesForm"
        status-icon
        ref="ruleForm"
    >
      <el-form-item label="用户名" prop="name">
        <el-input type="text" v-model="rulesForm.name"></el-input>
      </el-form-item>
      <el-form-item label="密码" prop="password">
        <el-input type="password"  v-model="rulesForm.password"></el-input>
      </el-form-item>

      <el-form-item>
        <el-button type="primary" @click="submitForm('ruleForm')" >提交</el-button>
        <el-button >重置</el-button>
      </el-form-item>

    </el-form>
  </div>
</template>
<script>
import {register} from '@/service/api';
export default {
  data() {
    return {
      rulesForm:{
        name:'',
        password:''
      },
      rules:{
        name:[
          {required:true,message:'请输入名字',trigger:"blur"},
          {min:1,max:5,message:"长度3-5",trigger:"blur"}
        ],
        password:[
          {required:true,message:'请输入密码',trigger:"blur"},
          {min:3,max:5,message:"长度3-5",trigger:"blur"}
        ]
      }
    };
  },
  methods: {
    submitForm(formName){
      this.$refs[formName].validate((valid)=>{
        if(valid){
          //如果校检通过，在这里向后端发送用户名和密码
          register({
            name: this.rulesForm.name,
            password: this.rulesForm.password,
          }).then((data)=>{
            console.log(data)
            if(data.code === 0){
              localStorage.setItem('token',data.data.token);
              window.location.href= '/';
            }
            if(data.code === 1){
              this.$message.error(data.mes)
            }
          });
        }else{
          console.log("error submit!!");
          return false;
        }
      });
    }
  }
}
</script>






<script>
export default {
  name: "login",
  data(){
    return{
      user:{},
      rules: {
        username: [
          {required: true, message: '请输入用户名', trigger: 'blur'},
          {min: 3, max: 5, message: '长度在 3 到 5 个字符', trigger: 'blur'}
        ],
        password: [
          {required: true, message: '请输入密码', trigger: 'blur'},
          {min: 3, max: 20, message: '长度在 3 到 20 个字符', trigger: 'blur'}
        ],
      }
    }
  },

  methods:{
    login() {
      this.$refs['userForm'].validate((valid) => {
        if (valid) {
          this.request.post("/user/login", this.user).then(res => {
            if (res.code === '200') {
              localStorage.setItem("user",JSON.stringify(res.data))  //存储用户信息到浏览器
              this.$router.push("/TrackingOfPollutants")
              this.$message.success('登录成功!')
            } else {
              this.$message.error(res.msg)
            }
          })
        }
      });
    }
  }
}

</script>

