import AMapLoader from "@amap/amap-jsapi-loader";
export default {
  data() {
    return {
      //假数据   经纬度
      lnglats: [[113.922282, 35.332887], [113.963101, 35.318516], [113.960801, 35.306263], [113.926809, 35.301255]]
    };
  },

  mounted() {
    this.carGPSIP();
  },

  methods: {
    carGPSIP() {
      AMapLoader.load({
        key: "0e5904b2829e2e25ffcdecc5685b2209",
        // 申请好的Web端开发者Key，首次调用 load 时必填
        version: "2.0",
        // 指定要加载的 JSAPI 的版本，缺省时默认为 1.4.15
        plugins: ["AMap.MoveAnimation", "AMap.ToolBar", "AMap.Scale", "AMap.MapType", "AMap.Geolocation", "AMap.HawkEye", "AMap.HeatMap"] // 需要使用的的插件列表，如比例尺'AMap.Scale'等

      }).then(AMap => {
        var map = new AMap.Map("container", {
          resizeEnable: true
        }); //初始化地图
        //信息窗口实例

        var infoWindow = new AMap.InfoWindow({
          offset: new AMap.Pixel(0, -30)
        }); //遍历生成多个标记点

        for (var i = 0, marker; i < this.lnglats.length; i++) {
          var marker = new AMap.Marker({
            position: this.lnglats[i],
            //不同标记点的经纬度
            map: map
          });
          marker.content = "我是第" + (i + 1) + "个Marker";
          marker.on("click", markerClick);
          marker.emit("click", {
            target: marker
          }); //默认初始化不出现信息窗体,打开初始化就出现信息窗体
        }

        function markerClick(e) {
          infoWindow.setContent(e.target.content);
          infoWindow.open(map, e.target.getPosition());
        }

        map.setFitView();
      });
    }

  }
};
