  created() {
    this.initCount();
  },

  mounted() {
    //DOM初始化完成进行地图初始化
    this.initMap();
  },

  methods: {
    initCount() {
      this.request.get("/nowatmospheredata").then(res => {
        this.lngLats = res;
      });
    },

    changValue() {
      if (!this.value1) {
        let data = [];
        this.heatMap.setDataSet({
          data: data,
          max: 100
        });
      } else {
        this.heatMap.setDataSet({
          data: this.data,
          max: 100
        });
      }
    },

    markerClick(e) {
      //offset用来偏移，第一个参数是左右，第一个参数是上下。左和上是负的，右和下是正的
      var infoWindow = new AMap.InfoWindow({
        offset: new AMap.Pixel(0, -30)
      });
      infoWindow.setContent(e.target.content);
      infoWindow.open(this.map, e.target.getPosition());
    },

    initMap() {
      AMapLoader.load({
        key: "0e5904b2829e2e25ffcdecc5685b2209",
        // 申请好的Web端开发者Key，首次调用 load 时必填
        version: "2.0",
        // 指定要加载的 JSAPI 的版本，缺省时默认为 1.4.15
        plugins: ['AMap.MoveAnimation', 'AMap.ToolBar', 'AMap.Scale', 'AMap.MapType', 'AMap.Geolocation', 'AMap.HawkEye', 'AMap.HeatMap'] // 需要使用的的插件列表，如比例尺'AMap.Scale'等

      }).then(AMap => {
        var _this = this;

        this.map = new AMap.Map("container", {
          //设置地图容器id
          resizeEnable: true,
          //mapStyle: 'amap://styles/normal',
          viewMode: "3D",
          //是否为3D地图模式
          zoom: 8,
          //初始化地图级别
          center: [118.3093, 32.3153],
          //初始化地图中心点位置
          doubleClickZoom: true,
          // 是否允许鼠标拖拽
          dragEnable: true,
          keyboardEnable: false
        });
        let marker;

        for (var i = 0; i < _this.lngLat.length; i++) {
          marker = new AMap.Marker({
            position: _this.lngLat[i],
            map: _this.map
          });
          marker.content = ["" + _this.lngLats[i].city + "   " + _this.lngLats[i].station + "<br/> AQI指数:" + _this.lngLats[i].aqi + "   CO指数:" + _this.lngLats[i].co + "<br/>" + "NO2指数:" + _this.lngLats[i].no2 + "   PM2.5指数:" + _this.lngLats[i].pm25 + "<br/> PM10指数:" + _this.lngLats[i].pm10 + "   SO2指数:" + _this.lngLats[i].so2 + "<br/> O3指数:" + _this.lngLats[i].o3 + "<br/>" + "<a href='https://ditu.amap.com/detail/B000A8URXB?citycode=110105'>详细信息</a>"]; // marker.content.push("<div></div>"+_this.lngLats[i].city+"   "+_this.lngLats[i].station+"<br/>")
          // marker.content.push("AQI指数:"+_this.lngLats[i].aqi+"   CO指数:"+_this.lngLats[i].co+"<br/>")
          // marker.content.push("NO2指数:"+_this.lngLats[i].no2+"   PM2.5指数:"+_this.lngLats[i].pm25+"<br/>")
          // marker.content.push("PM10指数:"+_this.lngLats[i].pm10+"   SO2指数:"+_this.lngLats[i].so2+"<br/>")
          // marker.content.push("O3指数:"+_this.lngLats[i].o3+"<br/>")

          marker.on("click", this.markerClick); //marker.emit("click", { target: marker });
        }

        this.heatMap = new AMap.HeatMap(this.map, {
          radius: 50,
          //给定半径
          opacity: [0, 1],
          gradient: {
            1: '#FF4C2F',
            0.8: '#FAA53F',
            0.6: '#FFF100',
            0.5: '#7DF675',
            0.4: '#5CE182',
            0.2: '#29CF6F'
          }
        });
        this.heatMap.setDataSet({
          data: this.data,
          max: 100
        }); // 在图面添加工具条控件，工具条控件集成了缩放、平移、定位等功能按钮在内的组合控件

        this.map.addControl(new AMap.ToolBar()); // 在图面添加比例尺控件，展示地图在当前层级和纬度下的比例尺

        this.map.addControl(new AMap.Scale()); // 在图面添加鹰眼控件，在地图右下角显示地图的缩略图

        this.map.addControl(new AMap.HawkEye({
          isOpen: true
        })); // 在图面添加定位控件，用来获取和展示用户主机所在的经纬度位置

        this.map.addControl(new AMap.Geolocation());
      }).catch(e => {
        console.log(e);
      });
