import { loadModules } from "esri-loader";
export default {
  name: "web-map",

  mounted() {
    // lazy load the required ArcGIS API for JavaScript modules and CSS
    loadModules(["esri/WebScene", "esri/views/SceneView", "esri/widgets/Expand", "esri/widgets/Weather", "esri/widgets/Daylight"], {
      css: true
    }).then(([WebScene, SceneView, Expand, Weather, Daylight]) => {
      const scene = new WebScene({
        portalItem: {
          id: "c56dab9e4d1a4b0c9d1ee7f589343516"
        }
      });
      const map = new Map({
        basemap: "satellite",
        ground: "world-elevation"
      });
      const view = new MapView({
        container: "viewDiv",
        map: map,
        qualityProfile: "high",
        environment: {
          weather: {
            type: "cloudy",
            // autocasts as new CloudyWeather({ cloudCover: 0.3 })
            cloudCover: 0.3
          },
          atmosphere: {
            quality: "high"
          },
          lighting: {
            waterReflectionEnabled: true,
            ambientOcclusionEnabled: true
          }
        }
      });
      const weatherExpand = new Expand({
        view: view,
        content: new Weather({
          view: view
        }),
        group: "top-right",
        expanded: true
      });
      const daylightExpand = new Expand({
        view: view,
        content: new Daylight({
          view: view
        }),
        group: "top-right"
      });
      view.ui.add([weatherExpand, daylightExpand], "top-right");
    });
  },

  beforeUnmount() {
    if (this.view) {
      // destroy the map view
      this.view.container = null;
    }
  }

};
