import Map from "@arcgis/core/Map";
import FeatureLayer from "@arcgis/core/layers/FeatureLayer.js";
import MapView from "@arcgis/core/views/MapView";
import FeatureForm from "@arcgis/core/widgets/FeatureForm";
export default {
  name: "Home",

  data() {
    return {
      areaName: "",
      visible: false
    };
  },

  mounted() {
    this.initMap();
  },

  methods: {
    initMap() {
      let highlight, editFeature;
      const template = {
        //titie:"站点信息",
        content: [{
          type: "fields",
          fieldInfos: [{
            fieldName: "city",
            label: "地区名称"
          }, {
            fieldName: "station",
            label: "站点名称"
          }]
        }]
      };
      const featurelayer = new FeatureLayer({
        outFields: ["city"],
        url: "http://localhost:6080/arcgis/rest/services/安徽站点/MapServer",
        popupTemplate: template
      });
      const map = new Map({
        basemap: "dark-gray-vector",
        layers: [featurelayer]
      }); //这个view相当于地图视图容器，指定了div等等属性

      const view = new MapView({
        container: "viewDiv",
        map: map,
        center: [117, 32],
        zoom: 8
      }); //监听地图点击事件

      view.on('click', event => {
        view.hitTest(event).then(response => {
          if ( //如果点击的是目标矢量图层 并且图层图形数量大于1 完成对目标矢量图层的筛选
          response.results.length > 0 && response.results[0].graphic && response.results[0].graphic.layer === featurelayer) {
            console.log(response.results[0].graphic.attributes.city);
            this.areaName = response.results[0].graphic.attributes.city + "(" + response.results[0].graphic.attributes.station + ")"; //数据双向绑定 获取点击图块的名称
            //调用selecFeature方法

            selectFeature(response.results[0].graphic.attributes[featurelayer.objectIdField]);
            this.visible = true; //let lat = Math.round(event.mapPoint.latitude * 1000000) / 1000000;
            //let lon = Math.round(event.mapPoint.longitude * 1000000) / 1000000;
            //alert(lon + ", " + lat);
          } else {
            this.visible = false;
          }
        });
      });

      function selectFeature(objectId) {
        featurelayer.queryFeatures({
          objectIds: [objectId]
        }).then(results => {
          if (results.features.length > 0) {
            //console.log(results.features[0])
            console.log(results.fields.length);
          }
        });
      }

      view.ui.components = [];
    }

  }
};
