import * as echarts from 'echarts';
export default {
  name: "Time_SpaceAnalysis",

  mounted() {
    this.initecharts();
  },

  methods: {
    initecharts() {
      var chartDom = document.getElementById('main');
      var myChart = echarts.init(chartDom);
      var option;
      const data = [];

      for (let i = 0; i < 5; ++i) {
        data.push(Math.round(Math.random() * 200));
      }

      option = {
        xAxis: {
          max: 'dataMax'
        },
        yAxis: {
          type: 'category',
          data: ['滁州市', '黄山市', '铜陵市', '马鞍山市', '安庆市', '芜湖市', '阜阳市', '六安市', '亳州市', '宿州市', '芜湖市', '合肥市', '六安市', '宣城市', '蚌埠市', '淮南市', '淮北市'],
          inverse: true,
          animationDuration: 300,
          animationDurationUpdate: 300,
          max: 16 //展示的柱状图数量是这个数字+1

        },
        series: [{
          realtimeSort: true,
          name: 'X',
          type: 'bar',
          data: data,
          label: {
            show: true,
            position: 'right',
            //展示位置
            valueAnimation: true //数据过渡平滑化

          }
        }],
        legend: {
          show: false //不展示图例

        },
        animationDuration: 0,
        animationDurationUpdate: 3000,
        animationEasing: 'linear',
        animationEasingUpdate: 'linear'
      };

      function run() {
        for (var i = 0; i < data.length; ++i) {
          if (Math.random() > 0.9) {
            data[i] += Math.round(Math.random() * 2000);
          } else {
            data[i] += Math.round(Math.random() * 200);
          }
        }

        myChart.setOption({
          series: [{
            type: 'bar',
            data
          }]
        });
      }

      setTimeout(function () {
        run();
      }, 0);
      setInterval(function () {
        run();
      }, 3000);
      option && myChart.setOption(option);
    }

  }
};
