import * as echarts from 'echarts';
export default {
  name: "echartsWatch",
  props: ['selectstaion'],
  watch: {
    selectstaion(newval) {
      this.request.get("/nowatmospheredata/GetData?station=" + newval + "").then(res => {
        this.aqi = res[0].aqi;
        this.city = res[0].city;
        this.station = res[0].station;
        this.CO = res[0].co;
        this.NO2 = res[0].no2;
        this.PM25 = res[0].pm25;
        this.PM10 = res[0].pm10;
        this.SO2 = res[0].so2;
        this.O3 = res[0].o3;
        this.echartsinit(this.aqi);
      });
    }

  },

  data() {
    return {
      aqi: "",
      CO: "",
      NO2: "",
      PM25: "",
      PM10: "",
      SO2: "",
      O3: "",
      city: "",
      station: ""
    };
  },

  mounted() {},

  methods: {
    initialize(e) {},

    echartsinit(aqi) {
      var chartDom = document.getElementById('main');
      var myChart = echarts.init(chartDom);
      var option;
      option = {
        series: [{
          type: 'gauge',
          startAngle: 180,
          endAngle: 0,
          min: 0,
          max: 1,
          splitNumber: 8,
          axisLine: {
            show: true,
            lineStyle: {
              width: 5,
              color: [[0.34, '#bbf5d3'], [0.67, '#ffed7e'], [1, '#d90f1e']]
            }
          },
          pointer: {
            icon: 'path://M12.8,0.7l12,40.1H0.7L12.8,0.7z',
            length: '50%',
            width: 5,
            offsetCenter: [0, '-20%'],
            itemStyle: {
              color: 'auto'
            }
          },
          axisTick: {
            length: 5,
            lineStyle: {
              color: 'auto',
              width: 10
            }
          },
          splitLine: {
            length: 0,
            lineStyle: {
              color: 'auto',
              width: 0
            }
          },
          axisLabel: {
            color: '#464646',
            fontSize: 12,
            distance: -25,
            textStyle: {
              //文字颜色
              color: '#fff'
            },
            formatter: function (value) {
              console.log(value);

              if (value === 0.875) {
                return '差';
              } else if (value === 0.5) {
                return '良';
              } else if (value === 0.125) {
                return '优';
              }

              return '';
            }
          },
          title: {
            offsetCenter: [0, '30%'],
            fontSize: 15,
            textStyle: {
              //文字颜色
              color: '#fff'
            }
          },
          detail: {
            fontSize: 15,
            offsetCenter: [0, '0%'],
            valueAnimation: true,
            formatter: function (value) {
              return Math.round(value * 100);
            },
            color: 'auto'
          },
          data: [{
            value: aqi / 100,
            name: 'AQI指数'
          }]
        }]
      };
      option && myChart.setOption(option);
    }

  }
};
