import * as echarts from 'echarts';
export default {
  name: "Home",

  data() {
    return {};
  },

  mounted() {
    this.drawChart();
    this.drawPie();
  },

  methods: {
    drawChart() {
      // 基于准备好的dom，初始化echarts实例  这个和上面的main对应
      let myChart = this.$echarts.init(document.getElementById("home")); // 指定图表的配置项和数据

      let option = {
        title: {
          text: "ECharts 入门示例"
        },
        tooltip: {},
        legend: {
          data: ["销量"]
        },
        xAxis: {
          data: ["衬衫", "羊毛衫", "雪纺衫", "裤子", "高跟鞋", "袜子"]
        },
        yAxis: {},
        series: [{
          name: "销量",
          type: "bar",
          data: [5, 20, 36, 10, 10, 20]
        }]
      }; // 使用刚指定的配置项和数据显示图表。

      myChart.setOption(option);
    },

    drawPie() {
      var chartDom = document.getElementById('pie');
      var myChart = echarts.init(chartDom);
      var option = {
        tooltip: {
          trigger: 'item'
        },
        legend: {
          top: '5%',
          left: 'center'
        },
        series: [{
          name: 'Access From',
          type: 'pie',
          radius: ['40%', '70%'],
          avoidLabelOverlap: false,
          itemStyle: {
            borderRadius: 10,
            borderColor: '#fff',
            borderWidth: 2
          },
          label: {
            show: false,
            position: 'center'
          },
          emphasis: {
            label: {
              show: true,
              fontSize: '40',
              fontWeight: 'bold'
            }
          },
          labelLine: {
            show: false
          },
          data: [{
            value: 1048,
            name: 'Search Engine'
          }, {
            value: 735,
            name: 'Direct'
          }, {
            value: 580,
            name: 'Email'
          }, {
            value: 484,
            name: 'Union Ads'
          }, {
            value: 300,
            name: 'Video Ads'
          }]
        }]
      };
      option && myChart.setOption(option);
    }

  }
};
