import * as echarts from 'echarts';

require('echarts/extension/bmap/bmap');

let BMap = {};
export default {
  name: "TrackingOfPollutants",

  data() {
    return {};
  },

  mounted() {
    this.creatMap();
  },

  methods: {
    loadScript() {
      var script = document.createElement("script");
      script.src = "https://api.map.baidu.com/api?v=2.0&ak=Tz3pnHtelKyQWPsW0B4Wh22Zib68w8U7&callback=initialize";
      document.body.appendChild(script);
    },

    //创建百度地图
    creatMap() {
      BMap = window.BMap;
      var map = new BMap.Map('map');
      map.centerAndZoom(new BMap.Point(116.404269, 39.916042), 12); //启用滚轮放大缩小

      map.enableScrollWheelZoom();
    }

  }
};
