import Map from "@arcgis/core/Map";
import FeatureLayer from "@arcgis/core/layers/FeatureLayer.js";
import MapView from "@arcgis/core/views/MapView";
export default {
  name: "Home",

  data() {
    return {};
  },

  mounted() {
    this.initMap();
  },

  methods: {
    initMap() {
      let highlight;
      const template = {
        titie: "{NAME99}",
        content: [{
          type: "fields",
          fieldInfos: [{
            fieldName: "NAME99",
            label: "地区名称"
          }, {
            fieldName: "AREA",
            label: "面积"
          }]
        }]
      };
      const featurelayer = new FeatureLayer({
        url: "http://localhost:6080/arcgis/rest/services/大气监测/MapServer",
        popupTemplate: template
      });
      const map = new Map({
        basemap: "streets-navigation-vector",
        layers: [featurelayer]
      }); //这个view相当于地图视图容器，指定了div等等属性

      const view = new MapView({
        container: "viewDiv",
        map: map,
        center: [117, 32],
        zoom: 6
      });
      view.ui.components = [];
    }

  }
};
