import Map from "@arcgis/core/Map";
import View from "@arcgis/core/views/MapView";
import MapImageLayer from "@arcgis/core/layers/MapImageLayer";
export default {
  data() {
    return {};
  },

  beforeMount() {},

  created() {},

  mounted() {
    this.initMap();
  },

  methods: {
    initMap() {
      const m_Map = new Map({
        basemap: "topo-vector"
      });
      const view = new View({
        container: "viewDiv",
        map: m_Map,
        center: [115, 32],
        zoom: 5
      });
      let layer = new MapImageLayer({
        url: "http://localhost:6080/arcgis/rest/services/MyMapService/MapServer"
      });
      m_Map.add(layer);
      view.ui.remove('attribution');
    }

  }
};
