import * as echarts from 'echarts';

require('echarts/extension/bmap/bmap');

export default {
  name: "TrackingOfPollutants",

  mounted() {
    this.loadScript();
    this.creatMap();
  },

  methods: {
    loadScript() {
      var script = document.createElement("script");
      script.src = "https://api.map.baidu.com/api?v=2.0&ak=Tz3pnHtelKyQWPsW0B4Wh22Zib68w8U7&callback=initialize";
      document.body.appendChild(script);
    },

    //创建百度地图
    creatMap() {
      const myChart = echarts.init(this.$refs.map);
      const option = {
        bmap: {
          center: [120.13066322374, 30.240018034923],
          zoom: 8,
          mapStyleV2: {
            styleId: '50aa27f5dc9a72aff522ccefb0da0e83'
          },
          roam: true
        }
      };
      myChart.setOption(option);
    }

  }
};
