export default {
  mounted() {
    var map = new BMapGL.Map("BaiDuMap"); // 创建地图实例 滁州市经纬度

    var point = new BMapGL.Point(118.316264, 32.303627);
    var scaleCtrl = new BMapGL.ScaleControl(); // 添加比例尺控件

    map.addControl(scaleCtrl);
    var zoomCtrl = new BMapGL.ZoomControl(); // 添加缩放控件

    map.addControl(zoomCtrl);
    var cityCtrl = new BMapGL.CityListControl(); // 添加城市列表控件

    var navi3DCtrl = new BMapGL.NavigationControl3D(); // 添加3D控件

    map.addControl(navi3DCtrl);
    map.addControl(cityCtrl); // 创建点坐标

    map.centerAndZoom(point, 14); // 初始化地图，设置中心点坐标和地图级别

    map.enableScrollWheelZoom(true); //开启鼠标滚轮缩放
    //map.setHeading();   //设置地图旋转角度

    map.setTilt(30); //设置地图的倾斜角度

    map.setMapType(BMAP_EARTH_MAP); // 设置地图类型为地球模式
    // 创建定位控件

    var locationControl = new BMapGL.LocationControl({
      // 控件的停靠位置（可选，默认左上角）
      anchor: BMAP_ANCHOR_TOP_RIGHT,
      // 控件基于停靠位置的偏移量（可选）
      offset: new BMapGL.Size(20, 20)
    }); // 将控件添加到地图上

    map.addControl(locationControl); // 添加定位事件

    locationControl.addEventListener("locationSuccess", function (e) {
      var address = '';
      address += e.addressComponent.province;
      address += e.addressComponent.city;
      address += e.addressComponent.district;
      address += e.addressComponent.street;
      address += e.addressComponent.streetNumber;
      alert("当前定位地址为：" + address);
    });
    locationControl.addEventListener("locationError", function (e) {
      alert(e.message);
    });
  }

};
