import * as echarts from 'echarts';
export default {
  name: "Home",

  data() {
    return {};
  },

  mounted() {
    this.drawChart();
  },

  methods: {
    init() {
      this.$nextTick(() => {
        this.initPic();
      });
    },

    initPic() {
      const chartDom = this.$refs.echartPicRef;
      const myChart = echarts.init(chartDom);
      const option = {
        xAxis: {
          type: "category",
          data: ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"]
        },
        yAxis: {
          type: "value"
        },
        series: [{
          data: [150, 230, 224, 218, 135, 147, 260],
          type: "line"
        }]
      };
      option && myChart.setOption(option);
    }

  }
};
